import {rerenderEntireTree} from "../render";

let state = {

    profilePage: {
        posts: [
            {id: 1, message: 'Hi, how are you?', likesCount: 17},
            {id: 2, message: 'Aliluya', likesCount: 11},
            {id: 3, message: 'Lalala', likesCount: 4}
        ],
        newPostText: "It"
    },


    dialogsPage: {
        messages: [
            {id: 1, message: 'Hi'},
            {id: 2, message: 'How is your'},
            {id: 3, message: 'Yo'},
            {id: 4, message: 'what yo did'},
            {id: 5, message: 'Yo yo'},
            {id: 6, message: 'he bro'}
        ],
        dialogs: [
            {id: 1, name: 'Dimych'},
            {id: 2, name: 'Andrew'},
            {id: 3, name: 'Sveta'},
            {id: 4, name: 'Sasha'},
            {id: 5, name: 'Viktor'},
            {id: 6, name: 'Valera'}


        ]
    }
}

 export let addPost =(postMessage) => {
    let newPost = {

        id:5,
        message: postMessage,
        likesCount: 0
    };

    state.profilePage.posts.push(newPost);
    rerenderEntireTree(state);
}

export let updateNewPostText =(newText) => {

    state.profilePage.newPostText = newText;
    rerenderEntireTree(state);
}


export default state;
