let store = {
    _state: {

        profilePage: {
            posts: [
                {id: 1, message: 'Hi, how are you?', likesCount: 17},
                {id: 2, message: 'Aliluya', likesCount: 11},
                {id: 3, message: 'Lalala', likesCount: 4}
            ],
            newPostText: ""
        },


        dialogsPage: {
            messages: [
                {id: 1, message: 'Hi'},
                {id: 2, message: 'How is your'},
                {id: 3, message: 'Yo'},
                {id: 4, message: 'what yo did'},
                {id: 5, message: 'Yo yo'},
                {id: 6, message: 'he bro'}
            ],
            dialogs: [
                {id: 1, name: 'Dimych'},
                {id: 2, name: 'Andrew'},
                {id: 3, name: 'Sveta'},
                {id: 4, name: 'Sasha'},
                {id: 5, name: 'Viktor'},
                {id: 6, name: 'Valera'}


            ]
        }
    },
    getState() {
        return this._state;
    },

    _callSubscriber() {
        console.log("State chabge")
    },

    addPost() {
        let newPost = {

            id: 5,
            message: this._state.profilePage.newPostText,
            likesCount: 0
        };

        this._state.profilePage.posts.push(newPost);
        this._state.profilePage.newPostText = "";
        this._callSubscriber(this._state);
    },

    updateNewPostText(newText) {

        this._state.profilePage.newPostText = newText;
        this._callSubscriber(this._state);
    },


    subscribe(observer) {
        this._callSubscriber = observer;
    }


}
export default store;

window.store = store;
